pragma solidity >=0.8.1;
// SPDX-License-Identifier: MIT

import "./FLT.sol";
import "./Ether.sol";
import "./SafeMath.sol";

contract FreelanceMarketplace {
    string public name = "Freelance Marketplace";
    address public owner;
    FLTToken private fltToken;
    Ether private etherToken;
    uint16 internal constant TOKEN_PER_ETHER = 100;
    bool private purchasingFLT = false;

    using SafeMath for uint256;

    event ArticleApproved(
        uint id,
        string Topic,
        uint reward,
        address payable client,
        address payable writer,
        Status status
    );

    enum Status {
        none,
        requested,
        accepted,
        submitted,
        completed,
        canceled
    }

    struct Article {
        uint id;
        string topic;
        address author;
        address owner;
        uint reward;
        uint deadline;
        uint createdAt;
        string content;
        uint wordCount;
        Status status;
    }

    uint public articlesCount = 0;
    mapping(string => Article) public articles;

    struct ArticleRequest {
        uint id;
        string topic;
        address payable client;
        address payable writer;
        uint256 reward;
        uint256 deadline;
        uint createdAt;
        Status status;
    }

    mapping(address => string[]) public userTopics;
    mapping(uint => string) public requestedTopics;

    uint public articleRequestsCount = 0;
    mapping(string => ArticleRequest) public articleRequests;

    event ArticleRequestCreated(
        uint id,
        string topic,
        uint reward,
        address payable client,
        uint deadline,
        uint createdAt
    );

    event ArticleRequestAccepted(
        uint requestId,
        string Topic,
        uint reward,
        address payable client,
        address payable writer,
        Status status
    );

    event ArticleSubmitted(
        uint articleId,
        string topic,
        uint reward,
        uint deadline,
        string content,
        address payable author,
        address payable client,
        uint submittedAt
    );

    event ArticleAccepted(
        uint articleId,
        string Topic,
        uint reward,
        uint deadline,
        address payable author,
        address payable client
    );

    event FLTPurchased(
        address buyer,
        uint etherAmount,
        uint FLTAmount,
        uint timestamp
    );

		event GiftedUser(
			address recipient,
			uint amount
		);

    constructor(FLTToken _fltToken, Ether _etherToken) {
        fltToken = _fltToken;
        etherToken = _etherToken;
        owner = msg.sender;
    }

    function getCurrentTime() internal view returns (uint256) {
        return block.timestamp * 1000;
    }

    function getAllUserTopics(
        address user
    ) public view returns (string[] memory) {
        return userTopics[user];
    }

		function gitfUserWithTokens(address _recepient, uint _amount) public onlyOwner {
			require(fltToken.transferFrom(owner, _recepient, _amount * (10 ** 18)), "Failed to gift user");

			emit GiftedUser(_recepient, _amount);
		}

    function getAllArticles()
        internal
        view
        returns (Article[] memory)
    {
        uint count = 0;
        for (uint i = 1; i < articleRequestsCount + 1; i++) {
            string memory topic = requestedTopics[i];
            Article memory article = articles[topic];

            if (article.id != 0) {
                count++;
            }
        }
        Article[] memory allArticles = new Article[](count);
        for (uint i = 1; i < articleRequestsCount + 1; i++) {
            string memory topic = requestedTopics[i];
            Article memory article = articles[topic];

            if (article.id != 0) {
                allArticles[i]  = article;
            }
        }
        return allArticles;
    }

    function getAllUserArticles(
        address user
    ) public view returns (Article[] memory) {
        // If user is owner, get all articles
        if (user == owner) {
            return getAllArticles();
        }

        uint count = 0;
        for (uint i = 1; i < articleRequestsCount + 1; i++) {
            string memory topic = requestedTopics[i];
            Article memory article = articles[topic];
            if (article.owner == user || article.author == user) {
                count++;
            }
        }

        Article[] memory userArticles = new Article[](count);

        for (uint i = 1; i < articleRequestsCount + 1; i++) {
            string memory topic = requestedTopics[i];
            Article memory article = articles[topic];
            if (article.owner == user || article.author == user) {
                userArticles[i - 1] = article;
            }
        }
        return userArticles;
    }

    function getLastNRequestedArticles(
        uint n
    ) public view returns (ArticleRequest[] memory) {
        if (n > articleRequestsCount) {
            n = articleRequestsCount;
        }
        // string[] memory topics = new string[n];
        string[] memory topics = new string[](n);

        for (uint i = 0; i < n; i++) {
            topics[i] = requestedTopics[articleRequestsCount - i];
        }
        ArticleRequest[] memory requests = new ArticleRequest[](topics.length);

        for (uint i = 0; i < topics.length; i++) {
            requests[i] = articleRequests[topics[i]];
        }

        return requests;
    }

    function requestArticle(
        string memory topic,
        uint256 deadline,
        uint256 reward
    ) public {
        require(
            articleRequests[topic].status == Status.none,
            "Article already requested"
        );

        // Only continue when we successfully debit the user
        require(
            fltToken.transferFrom(
                msg.sender,
                address(this),
                reward * (10 ** 18)
            ),
            "FLT transfer failed"
        );

        address payable client = payable(msg.sender);

        articleRequests[topic] = ArticleRequest(
            ++articleRequestsCount,
            topic,
            client,
            payable(address(0)),
            reward,
            deadline,
            getCurrentTime(),
            Status.requested
        );

        // Push topic into the user's topics mapping
        userTopics[client].push(topic);

        // Add topic to requested topics mapping
        requestedTopics[articleRequestsCount] = topic;

        // Emit event
        emit ArticleRequestCreated(
            articleRequestsCount,
            topic,
            reward,
            client,
            deadline,
            getCurrentTime()
        );
    }

    function acceptArticleRequest(string memory topic) public {
        require(
            articleRequests[topic].status != Status.accepted,
            "Article has already been accepted."
        );
        address payable writer = payable(msg.sender);
        // Get article request
        ArticleRequest memory request = articleRequests[topic];

        require(
            request.client != msg.sender,
            "You cannot work on your article request."
        );

        // Set the articleRequest's properties
        articleRequests[topic].writer = writer;
        articleRequests[topic].status = Status.accepted;

        // Emit event
        emit ArticleRequestAccepted(
            request.id,
            request.topic,
            request.reward,
            request.client,
            writer,
            Status.accepted
        );
    }

    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    function calculateReward(
        uint deadline,
        uint submittedAt,
        uint reward
    ) private pure returns (uint) {
        if (submittedAt <= deadline) {
            // Submitted on time or before the deadline
            return reward;
        }
        uint delay = SafeMath.div(SafeMath.sub(submittedAt, deadline), 1000);

        if (delay <= 3600) {
            // Submitted within one hour after the deadline
            // Pay 80% of reward
            return SafeMath.div(reward * 8, 10);
        } else if (delay <= 10800) {
            // Submitted within three hours after the deadline
            // Pay 60% of reward
            return SafeMath.div(reward * 6, 10);
        }
        // No reward for submissions later than three hours after the deadline
        return 0;
    }

    function submitArticle(
        string memory topic,
        uint wordCount,
        string memory content
    ) public {
        // Make sure the writer is submitting an article that has been requested
        require(
            articleRequests[topic].client != address(0),
            "Article has not been requested. Only requested articles can be submitted."
        );

        // Get article request
        ArticleRequest memory request = articleRequests[topic];
        articleRequests[topic].status = Status.submitted; // Set status
        address payable writer = payable(msg.sender);
        address payable client = payable(request.client);

        // Make sure the person working on the article is the one submitting it
        require(
            payable(request.writer) == writer,
            "Only the writer of an article can submit it."
        );

        uint currentTime = getCurrentTime();

        uint reward = calculateReward(
            request.deadline,
            currentTime,
            request.reward
        );

        if (reward < request.reward) {
            // Refund the client the difference since writer didn't submit on time.
            uint refundAmount = (request.reward - reward) * (10 ** 18);
            require(fltToken.transfer(client, refundAmount), "Refund failed");
        }

        articles[topic] = Article(
            ++articlesCount,
            topic,
            writer,
            request.client,
            reward,
            request.deadline,
            currentTime,
            content,
            wordCount,
            Status.submitted
        );

        emit ArticleSubmitted(
            articlesCount,
            topic,
            reward,
            request.deadline,
            content,
            writer,
            request.client,
            currentTime
        );
    }

    function approveArticle(string memory topic) public {
        // Get article
        Article memory article = articles[topic];

        // Make sure only owner of article (client) can approve it
        require(
            article.owner == msg.sender,
            "Only owner of article can approve it."
        );

        // Approve article
        articles[topic].status = Status.completed;

        // Pay freelancer
        uint num = 10 ** 18;
        uint articleReward = article.reward * num;
        address freelancer = article.author;
        require(
            fltToken.transfer(freelancer, articleReward),
            "FLT transfer failed"
        );

        // Emit ArticleApproved event
        emit ArticleApproved(
            article.id,
            topic,
            article.reward,
            payable(article.owner),
            payable(article.author),
            Status.completed
        );
    }

    function purchaseFLT(uint _etherAmount) public {
        // Check if amount is greater than 0
        if (_etherAmount < 0) {
            revert("Transfer mount cannot be 0");
        }

        require(
            !purchasingFLT,
            "Reentrancy protection: FLT is currently being purchased"
        );
        purchasingFLT = true;

        uint num = 10 ** 18;

        // Get user's ether balance before transfer
        uint balanceBefore = etherToken.balanceOf(msg.sender);

        // Transfer the required amount of etherToken from the buyer to the marketplace contract
        require(
            etherToken.transferFrom(
                msg.sender,
                address(this),
                _etherAmount * num
            ),
            "EtherToken transfer failed"
        );

        // ensure that the transfer was successful
        assert(
            etherToken.balanceOf(msg.sender) ==
                balanceBefore - _etherAmount * num
        );

        uint balance = _etherAmount * num * TOKEN_PER_ETHER;

        // Transfer the required amount of FLT from the marketplace contract to the buyer
        require(
            fltToken.transferFrom(owner, msg.sender, balance),
            "FLT transfer failed"
        );

        purchasingFLT = false;

        emit FLTPurchased(msg.sender, _etherAmount, balance, block.timestamp);
    }
}
